script_phases.script:
if [[ -d "$PODS_ROOT/YIM" ]]; then
  $PODS_ROOT/ObjectBox/generate_sources.sh -- --output "$PODS_ROOT/YIM/YIM/Classes/YIMGeneratedDB.swift" --model-json "$PODS_ROOT/YIM/YIM/Classes/YIMGeneratedDB.json" --no-statistics &&       echo $?
  echo "compile on release mode"
else
  {
    $PODS_ROOT/ObjectBox/generate_sources.sh -- --output "../../../SDK/Ios/YIM/Classes/YIMGeneratedDB.swift" --model-json "../../../SDK/Ios/YIM/Classes/YIMGeneratedDB.json" --no-statistics &&         echo $?
    echo "compile on develop demo mode"
  } || {
      $PODS_ROOT/ObjectBox/generate_sources.sh -- --output "../../YIM/Classes/YIMGeneratedDB.swift" --model-json "../../YIM/Classes/YIMGeneratedDB.json" --no-statistics &&           echo $?
      echo "compile on develop lib mode"
  }
fi
