description:
/**
 Convert UIColor to UIImage

 @param color Color to transfer
 @return UImage
 */
+(UIImage*)imageWithColor:(UIColor*) color;

/**
 Convert UIView to UIImage

 @param view view to convert
 @return image from view
 */
+(UIImage*)imageWithView:(UIView*)view Translucent:(BOOL)translucent;

/**
 Return sub image from given image to adapt device's screen

 @param image resouce image
 @return subImage
 */
+(UIImage *)subFittedImageFromImage:(UIImage *)image;

/**
 Filled given image with color

 @param color which color you choosed to draw in picture
 @return UIImage
 */
+(UIImage *)drawColorInImage:(UIColor *)color;

/**
 Rescale images

 @param image original image
 @param size size you wanna scale to
 @return scaledImage
 */
+ (UIImage *)imageScaledFromImage:(UIImage *)image Size:(CGSize)size;

/**
 Get current tint color, if not setted, returns clear color.

 @return UIColor
 */
+ (UIColor *)tintColor;

/**
 Set current color as tint color
 */
- (void)setTintColor;

/**
 Set color as tint color
 @param tintColor The tint color
 */
+ (void)setTintColor:(UIColor *)color;

/**
 Convert UIImage to UIColor

 @param pngName name of the Image
 @return UIColor
 */
+(UIColor *)colorWithImageName:(NSString *)pngName;

/**
 Convert hexString color into UIColor

 @param hexColor HexString of the color
 @return UIColor
 */
+ (UIColor *)colorWithHexString:(NSString *)hexColor;


/**
 Convert hexString color into UIColor

 @param hexColor HexString of the color
 @param alpha Alpha value of the color
 @return UIColor
 */
+ (UIColor *)colorWithHexString:(NSString *)hexColor Alpha:(CGFloat)alpha;


/**
 Get color with RGB Value

 @param RGBValue RGB value
 @return UIColor
 */
+ (UIColor *)colorWithRGB:(CGFloat)RGBValue;


/**
 Get color with RGB Value

 @param RGBValue RGB value
 @param alpha Alpha value
 @return UIColor
 */
+ (UIColor *)colorWithRGB:(CGFloat)RGBValue Alpha:(CGFloat)alpha;

/**
 Get color with RGB Value

 @param red Red value
 @param green Green value
 @param blue Blue value
 @return UIColor
 */
+ (UIColor *)colorWithR:(CGFloat)red G:(CGFloat)green B:(CGFloat)blue;


/**
 Get color with RGB Value

 @param red Red value
 @param green Green value
 @param blue Blue value
 @param alpha Alpha value
 @return UIColor
 */
+ (UIColor *)colorWithR:(CGFloat)red G:(CGFloat)green B:(CGFloat)blue Alpha:(CGFloat)alpha;
